import React, { useState, useEffect, useRef } from 'react';
import { useAI } from '../hooks/useAI';
import '../styles/components/AIChat.css';

/**
 * Компонент AIChat
 * 
 * Этот компонент отвечает за взаимодействие пользователя с ИИ для обработки новости.
 * 
 * Взаимодействует с:
 * - Хуком useAI для обработки запросов к ИИ
 * - CSS файлом для стилизации
 * 
 * Отвечает за:
 * - Отображение истории сообщений между пользователем и ИИ
 * - Обработку ввода пользователя и отправку запросов к ИИ
 * - Отображение ответов ИИ
 * - Автоматическую прокрутку чата
 */
const AIChat = ({ newsId }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const { sendMessage, aiResponse } = useAI(newsId);
  const chatEndRef = useRef(null);

  useEffect(() => {
    if (aiResponse) {
      setMessages(prev => [...prev, { type: 'ai', content: aiResponse }]);
    }
  }, [aiResponse]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim()) {
      setMessages(prev => [...prev, { type: 'user', content: input }]);
      sendMessage(input);
      setInput('');
    }
  };

  return (
    <div className="ai-chat">
      <div className="ai-chat__messages">
        {messages.map((message, index) => (
          <div key={index} className={`ai-chat__message ai-chat__message--${message.type}`}>
            {message.content}
          </div>
        ))}
        <div ref={chatEndRef} />
      </div>
      <form onSubmit={handleSubmit} className="ai-chat__input-form">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Введите ваш вопрос..."
          className="ai-chat__input"
        />
        <button type="submit" className="ai-chat__submit">Отправить</button>
      </form>
    </div>
  );
};

export default AIChat;
