import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/pages/Home.css';

/**
 * Компонент Home (Главная страница)
 * 
 * Этот компонент представляет собой главную страницу приложения.
 * 
 * Взаимодействует с:
 * - React Router для навигации по другим разделам приложения
 * 
 * Структура:
 * - Приветственное сообщение и краткое описание сервиса
 * - Быстрые ссылки на основные функции приложения
 * - Блоки для отображения статистики и последних обработанных новостей (заглушки)
 * 
 * Стилизация:
 * - Использует стили из '../styles/pages/Home.css'
 */
const Home = () => {
  return (
    <div className="home">
      <h1 className="home__title">Добро пожаловать в AI News Processor</h1>
      <p className="home__description">
        Наш сервис использует искусственный интеллект для обработки и анализа новостей об ИИ.
      </p>

      {/* Быстрые ссылки на основные функции */}
      <div className="home__quick-links">
        <Link to="/sources" className="home__link">Выбрать источник новостей</Link>
        <Link to="/processed" className="home__link">Просмотреть обработанные новости</Link>
        <Link to="/settings" className="home__link">Настройки</Link>
      </div>

      {/* Блок для отображения статистики (заглушка) */}
      <div className="home__stats">
        <h2>Статистика</h2>
        {/* Здесь можно добавить реальную статистику */}
        <p>Обработано новостей: 1000</p>
        <p>Активных пользователей: 50</p>
      </div>

      {/* Блок для отображения последних обработанных новостей (заглушка) */}
      <div className="home__recent-news">
        <h2>Последние обработанные новости</h2>
        {/* Здесь можно добавить список последних обработанных новостей */}
        <ul>
          <li>Новость 1</li>
          <li>Новость 2</li>
          <li>Новость 3</li>
        </ul>
      </div>
    </div>
  );
};

export default Home;
