import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/pages/Home.css';

/**
 * Компонент Home
 * 
 * Этот компонент отвечает за отображение главной страницы приложения.
 * 
 * Взаимодействует с:
 * - React Router для навигации по приложению
 * - CSS файлом для стилизации
 * 
 * Отвечает за:
 * - Отображение приветственного сообщения и краткого описания сервиса
 * - Предоставление быстрых ссылок на основные функции приложения
 * - Отображение статистики или последних обработанных новостей (если применимо)
 */
const Home = () => {
  return (
    <div className="home">
      <h1 className="home__title">Добро пожаловать в AI News Processor</h1>
      <p className="home__description">
        Наш сервис использует искусственный интеллект для обработки и анализа новостей об ИИ.
      </p>
      <div className="home__quick-links">
        <Link to="/sources" className="home__link">Выбрать источник новостей</Link>
        <Link to="/processed" className="home__link">Просмотреть обработанные новости</Link>
        <Link to="/settings" className="home__link">Настройки</Link>
      </div>
      <div className="home__stats">
        <h2>Статистика</h2>
        {/* Здесь можно добавить статистику, например, количество обработанных новостей */}
      </div>
      <div className="home__recent-news">
        <h2>Последние обработанные новости</h2>
        {/* Здесь можно добавить список последних обработанных новостей */}
      </div>
    </div>
  );
};

export default Home;
